mapper.select:

where cno=1
ORDER BY state asc, no asc
LIMIT #{offset}, #{pageSize}


WHERE user_id = #{id} AND cno=1
ORDER BY state asc, no desc
LIMIT #{sc.offset}, #{sc.pageSize}

mapper.update:
update asking
set   title = #{title}
    , contents = #{contents}
where no = #{no}

update asking
set   state = #{state}
where no = #{no}

mapper.delete:
DELETE FROM asking

     AND no = #{no}
     AND cno = #{cno}
     AND user_id = #{user_id}
