mapper.select:
SELECT o.user_id, o.name, o.address, o.phone, o.del_request, o.order_no, o.product_id AS o_product_id, o.size AS o_size, o.quantity, o.color AS o_color, o.status, o.order_date, o.tid, o.payment_method_type,
-- Product 정보 p.product_id AS p_product_id, p.product_name, p.color AS p_color, p.size AS p_size, p.price, p.discount, p.image, FLOOR(p.price - p.price / 100 * p.discount) AS dis_price, FLOOR((p.price - p.price / 100 * p.discount) * o.quantity) AS total, o.quantity AS p_quantity
FROM orders o JOIN product p ON o.product_id = p.product_id AND o.size = p.size WHERE o.order_no = #{order_no} AND o.size = #{size} AND o.product_id = #{product_id}

SELECT * FROM orders
WHERE user_id = #{id} AND order_no = #{order_no}
AND product_id = #{product_id}
AND size = #{size}

    AND status NOT IN ('반품대기중' , '교환대기중', '반품처리중', '교환처리중', '반품완료', '교환완료', '취소완료');

mapper.update:
UPDATE orders
is_deleted = #{is_deleted}, status = #{status}, tid = #{tid}, payment_method_type = #{payment_method_type}, issuer_corp = #{issuer_corp}
WHERE order_no = #{order_no} AND user_id = #{id}

UPDATE orders
SET status = #{status}
WHERE order_no = #{order_no}
  AND user_id = #{id}
  AND product_id = #{product_id}
   AND size = #{size}

mapper.select:
SELECT o.*
FROM orders o
JOIN (
SELECT DISTINCT order_no
FROM orders

    status = #{category}
    AND ${option} LIKE concat('%',#{keyword},'%')
    AND status NOT LIKE concat('%', '결제', '%')

ORDER BY order_no ASC
LIMIT #{offset}, #{pageSize}
) sub ON o.order_no = sub.order_no
ORDER BY o.order_no ASC, o.product_id ASC;
