mapper.select:
SELECT * FROM orders
WHERE user_id = #{id} AND order_no = #{order_no}
AND product_id = #{product_id}
AND size = #{size}

    AND status NOT IN ('반품접수', '교환접수', '교환중', '반품완료', '교환완료', '취소완료');

mapper.update:
UPDATE orders
is_delete = #{is_deleted}, status = #{status}, tid = #{tid}, payment_method_type = #{payment_method_type}, issuer_corp = #{issuer_corp}
WHERE order_no = #{order_no} AND user_id = #{id}

UPDATE orders
SET status = #{status}
WHERE order_no = #{order_no}
  AND user_id = #{id}
  AND product_id = #{product_id}
   AND size = #{size}

mapper.select:
SELECT o.*
FROM orders o
JOIN (
SELECT DISTINCT order_no
FROM orders

    status = #{category}
    AND ${option} LIKE concat('%',#{keyword},'%')
    AND status NOT LIKE concat('%', '결제', '%')

ORDER BY order_no ASC
LIMIT #{offset}, #{pageSize}
) sub ON o.order_no = sub.order_no
ORDER BY o.order_no ASC, o.product_id ASC;
