SET stock = #{stock}
WHERE product_id = #{product_id} AND size = #{size}

UPDATE product
bought_cnt = #{bought_cnt}, wish_cnt = #{wish_cnt}, rating = #{rating}, review_cnt = #{review_cnt}, stock = #{stock}, discount = #{discount}, price = #{price},
WHERE product_id = #{product_id} AND size=#{size}
