mapper.update:
UPDATE report_review
SET state = 0
WHERE no = #{no}

mapper.select:
SELECT count(*) FROM report_review

     state = #{state}
     AND user_id = #{id}
     AND reporter_id =  #{reporter}
     AND rno =  #{rno}

SELECT * FROM report_review

     state = #{state}
     AND user_id = #{id}
     AND reporter_id =  #{reporter}
     AND rno =  #{rno}

ORDER BY no desc
LIMIT #{sc.offset}, #{sc.pageSize}
