mapper.update:
UPDATE review
contents = #{contents}, rating = #{rating}, reg_date = #{reg_date}, up_date = #{up_date}, whether = #{whether}, image = #{image}, report_cnt = #{report_cnt},
WHERE rno = #{rno} AND user_id = #{user_id}

UPDATE review
SET whether = #{whether}
WHERE rno = #{rno}

UPDATE review
set whether = #{whether}
WHERE order_no = #{order_no} AND product_id = #{product_id} AND user_id = #{id} AND size = #{size}

mapper.select:
SELECT AVG(rating) AS average_value
FROM review
where whether = 1 AND product_id = #{product_id}

SELECT count(*) FROM review
WHERE
 user_id = #{id}
product_id = #{product_id}
    AND whether = #{whether}

SELECT * FROM review
WHERE true
AND user_id = #{id}
AND product_id = #{product_id}
    AND whether = #{whether}

    ORDER BY reg_date desc


    ORDER BY rating desc, rno desc

LIMIT #{sc.offset}, #{sc.pageSize}
